        public static int Racunaj(string kod)
        {
            int suma = 0;
            int broj = 1;
            int duzina = kod.Length;
            for (int i = 0; i < duzina; i++)
            {   
                if (suma == -1)
                {
                    return broj;
                }
                if (kod[i] is '(')
                {
                    suma += 1;
                    broj++;
                }
                else
                {
                    suma -= 1;
                    broj++;
                }
            }
            return suma;
        }

